import React from 'react';
import './App.scss';
import { List } from './List/List';
import { FAMILIES } from './consts/families';
import { INSTRUMENTS } from './instruments.json';
import { Instrument } from './interfaces/Instrument';
import { InstrumentDetails } from './InstrumentDetails/InstrumentDetails';

class App extends React.Component {
  private lists: React.ClassicElement<typeof List>[] = [
    (<div key="0" className="col-3">
      <List
        name="Families"
        listItems={Object.values(FAMILIES)}
        itemSelected={this.selectFamily.bind(this)}
      />
    </div>)
  ];

  private instrumentDisplayed: any;

  render() {
    return (<div className="App">
      <h1>Common Instrument Ranges</h1>
      <div className="row">
          {this.lists}
          <div className="col-6" key="2">
            {this.instrumentDisplayed}
          </div>
      </div>
    </div>
    );
  }

  private selectFamily(family: string) {
    this.lists = [
      (
        <div key="0" className="col-3">
          <List
            name="Families"
            listItems={Object.values(FAMILIES)}
            itemSelected={this.selectFamily.bind(this)}
          />
        </div>
      ),
      (
        <div key="1" className="col-3">
          <List
            name="Instruments"
            listItems={this.filterInstruments(family)}
            itemSelected={this.selectInstrument.bind(this)}
          />
        </div>
      ),
    ];
    this.instrumentDisplayed = null;
    this.forceUpdate();
  }

  private selectInstrument(instrument: string) {
    this.instrumentDisplayed = (
      <InstrumentDetails instrument={this.chooseInstrument(instrument)} />
    )
    this.forceUpdate();
  }

  private filterInstruments(familyName: string) {
    const familyInstruments: Instrument[] = JSON.parse(JSON.stringify(
      INSTRUMENTS.filter((instrument) => {
        return instrument.family === familyName;
      })
    ));
    return familyInstruments.map((instrument: Instrument) => {
      return instrument.name;
    });
  }

  private chooseInstrument(instrument: string) {
    const inst = INSTRUMENTS.find((inst: Instrument) => {
      return inst.name === instrument;
    });
    const blankInst: Instrument = {
      name: '',
      key: '',
      imgUrl: '',
      family: '',
      rangeTop: '',
      rangeBottom: ''
    }
    return inst  || blankInst;
  }
}

export default App;
