import React from 'react';
import './App.scss';
import { List } from './List/List';
import { FAMILIES } from './consts/families';
import { INSTRUMENTS } from './instruments.json';
import { Instrument } from './interfaces/Instrument';

class App extends React.Component {
  private lists: React.ClassicElement<typeof List>[] = [
    (<div className="col-3">
      <List
        name="Families"
        key="0"
        listItems={Object.values(FAMILIES)}
        itemSelected={this.selectFamily.bind(this)}
      />
    </div>)

  ];
  render() {
    return (<div className="App">
      <div className="row">
          {this.lists}
      </div>
    </div>
    );
  }

  private selectFamily(family: string) {
    this.lists = [
      (
        <div className="col-3">
          <List
            name="Families"
            key="0"
            listItems={Object.values(FAMILIES)}
            itemSelected={(familyName: string) => this.selectFamily(familyName)}
          />
        </div>
      ),
      (
        <div className="col-3">
          <List
            name="Instruments"
            key="1"
            listItems={this.filterInstruments(family)}
            itemSelected={(instrument: string) => this.selectInstrument(instrument)}
          />
        </div>
      ),
    ];
    this.setState({});
  }

  private selectInstrument(instrument: string) {
    console.log(instrument);
  }

  private filterInstruments(familyName: string) {
    const familyInstruments: Instrument[] = JSON.parse(JSON.stringify(
      INSTRUMENTS.filter((instrument) => {
        return instrument.family === familyName;
      })
    ));
    return familyInstruments.map((instrument: Instrument) => {
      return instrument.name;
    });
  }
}

export default App;
