import React from 'react';
import './App.scss';
import { List } from './components/List/List';
import { FAMILIES } from './consts/families';
import { INSTRUMENTS } from './instruments.json';
import { Instrument } from './interfaces/Instrument';
import { InstrumentDetails } from './components/InstrumentDetails/InstrumentDetails';
const axios = require('axios').default;

interface AppState {
  families: string[];
  lists: React.ClassicElement<typeof List>[];
  instrumentDisplayed: React.ClassicElement<typeof InstrumentDetails>[];
}

const InitialState: AppState = {
  families: [],
  lists: [],
  instrumentDisplayed: [],
}

class App extends React.Component<{}, AppState> {
  state: AppState = InitialState;

  constructor() {
    super(InitialState, {});
    axios.get('/api/families').then((resp: string[]) => {
      this.state.families = resp;
      this.state.lists = [(<div key="0" className="col-3">
      <List
        name="Families"
        listItems={Object.values(FAMILIES)}
        itemSelected={this.selectFamily.bind(this)}
      />
    </div>)];
    });
  }

  render() {
    return (<div className="App">
      <h1>Common Instrument Ranges</h1>
      <div className="row">
          {this.state.lists}
          <div className="col-6" key="2">
            {this.state.instrumentDisplayed}
          </div>
      </div>
    </div>
    );
  }

  private selectFamily(family: string) {
    axios.get(`/api/instruments?family=${family}`).then((resp: Instrument[]) => {
      this.state.lists = [
        (
          <div key="0" className="col-3">
            <List
              name="Families"
              listItems={Object.values(FAMILIES)}
              itemSelected={this.selectFamily.bind(this)}
            />
          </div>
        ),
        (
          <div key="1" className="col-3">
            <List
              name="Instruments"
              listItems={this.filterInstruments(resp)}
              itemSelected={this.selectInstrument.bind(this)}
            />
          </div>
        ),
      ];
      this.state.instrumentDisplayed = [];
    });

  }

  private selectInstrument(instrument: string) {
    this.state.instrumentDisplayed = [(
      <InstrumentDetails instrument={this.chooseInstrument(instrument)} />
    )];
    this.forceUpdate();
  }

  private filterInstruments(instruments: Instrument[]) {
    return instruments.map((instrument: Instrument) => {
      return instrument.name;
    });
  }

  private chooseInstrument(instrument: string) {
    const inst = INSTRUMENTS.find((inst: Instrument) => {
      return inst.name === instrument;
    });
    const blankInst: Instrument = {
      name: '',
      key: '',
      imgUrl: '',
      family: '',
      rangeTop: '',
      rangeBottom: ''
    }
    return inst  || blankInst;
  }
}

export default App;
